       console.log(this.state.index)
         if(this.ImageTag) {
          this.ImageTag.src = "http://localhost:4000/img/"+this.state.ImageNames[this.state.index];
       }
    })
    .catch(err => { // then print response status
    console.log("fail")
    console.log(err)
    })
  }

componentDidMount(){
  var userName = this.props.name
